def convert_temp(unit_in, temp, unit_out):
    units = ['Celsius', 'Fahrenheit', 'Kelvin']
    if unit_in not in units or unit_out not in units:
        return 'Invalid input'
    if unit_in == 'Celsius':
        if unit_out == 'Fahrenheit':
            return (temp * 9/5) + 32
        elif unit_out == 'Kelvin':
